// import React from 'react';

// function TrafficPage() {

//     return (
//         <div className="traffic-page">
//             <h1>교통 정보 페이지</h1>
//             {/* 교통 데이터 관련 내용 */}
//         </div>
//     );
// }

// export default TrafficPage;


import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react';

const TrafficPage = () => {
  const trafficHotspots = [
    { id: 1, name: "청량리역", lat: 37.5803, lng: 127.0474, congestion: "매우 혼잡" },
    { id: 2, name: "동대문역", lat: 37.5715, lng: 127.0090, congestion: "보통" },
  ];

  return (
    <div className="flex h-screen">
      {/* 지도 영역 */}
      <div className="flex-1">
        <MapContainer center={[37.574, 127.04]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {trafficHotspots.map(spot => (
            <Marker key={spot.id} position={[spot.lat, spot.lng]}>
              <Popup>
                <strong>{spot.name}</strong><br />
                혼잡도: {spot.congestion}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* 사이드바 */}
      <div className="w-80 bg-white shadow-lg p-4 overflow-y-auto">
        <h2 className="text-lg font-bold mb-4">🚦 교통 혼잡도 안내</h2>
        <ul className="mb-6">
          <li className="flex items-center mb-2"><span className="w-4 h-4 bg-red-500 inline-block mr-2"></span> 매우 혼잡</li>
          <li className="flex items-center mb-2"><span className="w-4 h-4 bg-yellow-400 inline-block mr-2"></span> 보통</li>
          <li className="flex items-center mb-2"><span className="w-4 h-4 bg-green-500 inline-block mr-2"></span> 원활</li>
        </ul>

        <h3 className="text-md font-semibold mb-2">예측 데이터</h3>
        <p className="text-sm mb-4">다음 1시간 동안 주요 도로 혼잡 예상 구간: 청량리역, 장안동 사거리</p>

        <h3 className="text-md font-semibold mb-2">실시간 알림</h3>
        <div className="bg-red-100 border-l-4 border-red-500 p-2 text-sm">
          🚨 장안동 사거리 차량 사고로 인한 정체
        </div>
      </div>
    </div>
  );
};

export default TrafficPage;
